import React, { useState } from 'react';

const Contact =()=>{
  const[data,setData] = useState({
     fullname:"",
     email:"",
     address:"",
     phone:"",
     message:"",
  });

  const InputEvent =(event)=>{
    const{name,value} = event.target;
    setData((preVal) =>{
        return{
          ...preVal,
          [name]:value,
        }
    });

  }
  const fromSubmit = async(e) =>{
    e.preventDefault();
    // alert(`${data.fullname}`);
    const{fullname,email,address,phone,message}=data;

    if (fullname && email && address && phone && message){
      const res = await fetch("https://form-data01-default-rtdb.firebaseio.com/form.json",
        {
          method:"POST",
          headers:{
          "Content-Type":"application/json",
          },
          body:JSON.stringify({
            fullname,email,address,phone,message,
          }),
        }
      );

      if (res) {
       setData({
         fullname:"",
         email:"",
         address:"",
         phone:"",
         message:"",
       });
       alert("Data Store Successfully");
      }

    } else{
      alert("Pleaze fill all the Data")

    }

  };
  return(
    <>
     <div className="contact-text">
       <h1 className="text-center">Contact Us</h1>
     </div>
     <div className="containercontact_div">
       <div className="row">
         <div className="my-5 col-md-6 col-10 mx-auto">
         <form onSubmit={fromSubmit} method="POST">
            <div class="mb-3">
            <label htmlFor="exampleFormControlInput1" className="form-label">Name</label>
            <input type="text" className="form-control" id="name" name="fullname" autoComplete="off" required value={data.fullname} onChange={InputEvent} placeholder="Full Name" />

            <label htmlFor="exampleFormControlInput1" className="form-label">Email </label>
            <input type="email" className="form-control" id="exampleFormControlInput1" name="email" autoComplete="off" required value={data.email} onChange={InputEvent} placeholder="name@example.com" />

            <label htmlFor="exampleFormControlInput1" className="form-label">Address</label>
            <input type="text" className="form-control" id="address" name="address" autoComplete="off" required value={data.address} onChange={InputEvent} placeholder="Full Address" />

            <label htmlFor="exampleFormControlInput1" className="form-label">Mobile Number</label>
            <input type="text" className="form-control" id="mobile" name="phone" autoComplete="off" required value={data.phone} onChange={InputEvent} placeholder="Enter Mobile Number" />

            <div className="mb-3">
              <label htmlFor="exampleFormControlTextarea1" className="form-label">Example textarea</label>
              <textarea className="form-control" id="exampleFormControlTextarea1" name="message" autoComplete="off" required value={data.message} onChange={InputEvent} rows={3} defaultValue={""} />
            </div>
          </div>

          <button type="submit" className="btn btn-outline-primary" onClick={fromSubmit}>Submit</button>

         </form>
         </div>

       </div>

     </div>
    </>
  );
};
export default Contact;
