export const CartReducer = (state,action) => {
    const {shopingCard,totalPrice,qty}=state;
    let product;
    let index;
    let updatePrice;
    let updateQty;

    switch(action.type){
        case "ADD_TO_CART":
            const check = shopingCard.find(product=>product.id === action.id);
            if (check){
                return state;
            }else{
                product=action.product;
                updateQty=qty+1;
                updatePrice=totalPrice+product.price;
                return{shopingCard:[product,...shopingCard],totalPrice:updatePrice,qty:updateQty};
            }
            break;

            default:
                return state;
    }
}
