import React,{useContext} from 'react'
import { CartContext } from '../Global/CartContext'

const Cart = () => {
    const [shopingCard,totalPrice,qty,dispatch] =useContext(CartContext);

    return (
        <>
           <div className="container">
               <div className="cart-container" style={{marginTop:'100px'}}>
                   {shopingCard.length>0 ? shopingCard.map(cart=>(
                       <div className="cart" key={cart.id}>
                           <div className="cart-image">
                              <img src={cart.image} alt="No img"/>
                           </div>
                       </div>
                   )):"Sorry the cart is empty..."}

               </div>
           </div> 
        </>
    )
}

export default Cart
