import React,{useState,useContext} from 'react'
import { ProductContext } from '../Global/ProductContext'
import {CartContext} from '../Global/CartContext'


const Search = () => {
  const {dispatch}=useContext(CartContext)
  const {products}= useContext(ProductContext);
  // console.log(products);
  const [filter,setFilter]=useState('');
  const searchText=(event)=>{
    setFilter(event.target.value);
  }
  let dataSearch=products.filter(item=>{
    return Object.keys(item).some(key=>
      item[key].toString().toLowerCase().includes(filter.toString().toLowerCase()))
  });
   console.log(dataSearch);
  return (
      <> 



       <div className="searchBox">
         <input className="searchInput" type="text" value={filter} onChange={searchText.bind(this)} placeholder="Search" />
          <button className="searchButton" href="#">
          <i className="material-icons">
              search
          </i>
         </button>



        { filter === "" ? null : dataSearch.map((item,index)=>{

        return(

          <div className="product-Card">
          <div className="product-Id" key={item.id}>

              <div className="product-img">
                  <img src={item.image} alt="Not Found"/>
              </div> 
              <div className="product-detail">
                  <div className="product-name">
                      {item.name}
                  </div> 
                  <div className="product-price">
                      {item.price}
                  </div>       
              </div>  
              <div className="product-add" onClick={()=> dispatch({type:'ADD_TO_CART',id:item.id,item})}>Add Card</div> 
              {item.status==='new' ? <div className="new">New</div>:''}
              {item.status==='popular' ? <div className="popular">Popular</div>:''}    
          </div>    
          </div> 


         )

        })}


      </div>

    </>
  )
}

export default Search;
